CREATE OR REPLACE FUNCTION obter_dias_entre_datas_uteis ( dt_inicial_p timestamp, dt_final_p timestamp) RETURNS bigint AS $body$
DECLARE


dt_inicial_w	timestamp;
dt_final_w		timestamp;
qt_dias_w		bigint := 0;
ie_dia_semana_w	varchar(5);


BEGIN
dt_inicial_w	:= trunc(dt_inicial_p);
dt_final_w		:= trunc(dt_final_p);

if (dt_inicial_w = dt_final_w)then
	qt_dias_w	:= 0;
else
	while(dt_inicial_w <= dt_final_w) loop
		begin

		select	obter_cod_dia_semana(dt_inicial_w)
		into STRICT	ie_dia_semana_w
		;

		if (ie_dia_semana_w not in (1,7)) then
			qt_dias_w	:= qt_dias_w + 1;
		end if;

		end;

	dt_inicial_w	:= dt_inicial_w + 1;
	end loop;

end if;

return qt_dias_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
