CREATE OR REPLACE FUNCTION obter_dias_entre_datas (dt_inicial_p timestamp, dt_final_p timestamp) RETURNS bigint AS $body$
DECLARE


dt_inicial_w	timestamp;
dt_final_w	timestamp;
qt_dias_w	bigint	:= 0;


BEGIN
if (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') then
	dt_inicial_w	:= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_inicial_p);
	dt_final_w	:= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_final_p);

	qt_dias_w	:= round(dt_final_w - dt_inicial_w);
end if;

RETURN	qt_dias_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
