CREATE OR REPLACE FUNCTION obter_dias_horas_internacao ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
qt_dias_internacao_w	double precision := 0;
dt_entrada_w		timestamp;
dt_alta_w		timestamp;
qt_minutos_w		double precision := 0;


BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	select	dt_entrada, 
		coalesce(dt_alta, clock_timestamp()) 
	into STRICT	dt_entrada_w, 
		dt_alta_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
	 
	qt_dias_internacao_w	:= (dt_alta_w - dt_entrada_w);
	qt_minutos_w		:= (qt_dias_internacao_w - trunc(qt_dias_internacao_w)) * 1440;
	return lpad(to_char(trunc(qt_dias_internacao_w)),3,'0') || ' / ' || lpad(obter_hora_duracao(qt_minutos_w),5,'0');
else 
	return '';
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
