SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dias_idade_gestacional (nr_seq_laudo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


qt_idade_w			bigint;
nr_prescricao_w		bigint;
dt_exame_w			timestamp;
dt_menstruacao_w	timestamp;



BEGIN

/*
	Opção
		0 - Dias
		1 - Semana
*/
select	coalesce(max(nr_prescricao),0),
		trunc(coalesce(max(dt_exame),clock_timestamp()))
into STRICT 	nr_prescricao_w,
		dt_exame_w
from	laudo_paciente
where	nr_sequencia = nr_seq_laudo_p;


select	coalesce(max(dt_mestruacao),clock_timestamp())
into STRICT	dt_menstruacao_w
from	prescr_medica
where	nr_prescricao =  nr_prescricao_w;

if (ie_opcao_p = '0') then

	select	mod(obter_dias_entre_datas(dt_menstruacao_w,dt_exame_w),7)
	into STRICT	qt_idade_w
	;

elsif (ie_opcao_p = '1') then

	select	trunc(obter_dias_entre_datas(dt_menstruacao_w,dt_exame_w)/7)
	into STRICT	qt_idade_w
	;

end if;

return	qt_idade_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
