CREATE OR REPLACE FUNCTION obter_dias_internacao_hcu ( nr_atendimento_p bigint ) RETURNS bigint AS $body$
DECLARE

    qt_dias_internacao_w   bigint := 0;
    dt_entrada_unidade_w   timestamp;
    dt_saida_unidade_w     timestamp;

BEGIN
    IF (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') THEN
        SELECT
            MAX(establishment_timezone_utils.startofday(dt_entrada_unidade)),
            MAX(establishment_timezone_utils.startofday(coalesce(dt_saida_unidade, clock_timestamp())))
        INTO STRICT
            dt_entrada_unidade_w,
            dt_saida_unidade_w
        FROM
            atend_paciente_unidade   a,
            setor_atendimento        b
        WHERE
            nr_atendimento = nr_atendimento_p
            AND a.cd_setor_atendimento = b.cd_setor_atendimento
            AND b.ie_hcu = 'S';

        qt_dias_internacao_w := ( dt_saida_unidade_w - dt_entrada_unidade_w ) + 1;
    END IF;

    RETURN qt_dias_internacao_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
