CREATE OR REPLACE FUNCTION obter_dias_internacao_setor ( nr_atendimento_p bigint, cd_setor_atendimento_p bigint, dt_inicial_p timestamp, dt_final_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_total_dias_w	bigint;


BEGIN

	select	sum(qt_total_c)
	into STRICT	qt_total_dias_w
	from
	(
	SELECT	distinct
		obter_dias_entre_datas(dt_entrada_unidade,dt_saida_unidade) qt_total_c
	from	atend_paciente_unidade
	where	((dt_entrada_unidade between dt_inicial_p and dt_final_p + 86399/86400)
		or (dt_saida_unidade between dt_inicial_p and dt_final_p + 86399/86400)
		or (dt_entrada_unidade < dt_inicial_p and coalesce(dt_saida_unidade::text, '') = ''))
	and	cd_setor_atendimento = cd_setor_atendimento_p
	and	nr_atendimento = nr_atendimento_p
	group by 	nr_atendimento, dt_entrada_unidade, dt_saida_unidade
	) alias7;

return coalesce(qt_total_dias_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
