CREATE OR REPLACE FUNCTION obter_dias_internacao_uti ( nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_dias_internacao_w	bigint := 0;
dt_entrada_unidade_w		timestamp;
dt_saida_unidade_w	timestamp;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	max(ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_entrada_unidade)),
		max(ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(coalesce(dt_saida_unidade, clock_timestamp())))
	into STRICT	dt_entrada_unidade_w,
		dt_saida_unidade_w
	from	atend_paciente_unidade a,
		setor_atendimento b
	where	nr_atendimento = nr_atendimento_p
	and	a.cd_setor_atendimento = b.cd_setor_atendimento
	and	b.cd_classif_setor = '4';
	
	qt_dias_internacao_w := (dt_saida_unidade_w - dt_entrada_unidade_w) + 1;
end if;

return qt_dias_internacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
