CREATE OR REPLACE FUNCTION obter_dias_intern_uti_periodo ( nr_atendimento_p bigint, dt_periodo_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_dias_internacao_w	bigint := 0;
dt_entrada_unidade_w		timestamp;
dt_saida_unidade_w	timestamp;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	max(trunc(dt_entrada_unidade)),
		max(trunc(coalesce(dt_saida_unidade, dt_periodo_p)))
	into STRICT	dt_entrada_unidade_w,
		dt_saida_unidade_w
	from	atend_paciente_unidade a
	where	nr_atendimento = nr_atendimento_p
	and	obter_classif_setor(cd_setor_atendimento) = 4;

	qt_dias_internacao_w := (dt_saida_unidade_w - dt_entrada_unidade_w) + 1;
end if;

return qt_dias_internacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
