SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dias_interv_protoc ( nr_seq_paciente_p bigint, nr_ciclo_p bigint) RETURNS bigint AS $body$
DECLARE



cd_protocolo_w		bigint;
nr_seq_medicacao_w	bigint;
qt_dias_intervalo_w	bigint;


BEGIN


select	max(cd_protocolo),
		max(nr_seq_medicacao)
into STRICT	cd_protocolo_w,
		nr_seq_medicacao_w
from	paciente_setor
where	nr_seq_paciente	= nr_seq_paciente_p;

if (cd_protocolo_w IS NOT NULL AND cd_protocolo_w::text <> '') and (nr_seq_medicacao_w IS NOT NULL AND nr_seq_medicacao_w::text <> '') then
	select	max(QT_DIAS_INTERVALO)
	into STRICT	qt_dias_intervalo_w
	from	PROTOCOLO_MEDICACAO_INTEV
	where	cd_protocolo	= cd_protocolo_w
	and		nr_seq_medicacao = nr_seq_medicacao_w
	and		nr_ciclo_p between NR_CICLO_INICIAL and NR_CICLO_FINAL;
end if;

return	qt_dias_intervalo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
