CREATE OR REPLACE FUNCTION obter_dias_isolamento (dt_inicio_p timestamp, dt_termino_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_dias_isolamento_w	bigint := 0;

dt_termino_w	timestamp;


BEGIN

if (dt_inicio_p IS NOT NULL AND dt_inicio_p::text <> '') then

	if (dt_termino_p IS NOT NULL AND dt_termino_p::text <> '') then
		dt_termino_w := dt_termino_p;
	else
		dt_termino_w := clock_timestamp();
	end if;

	qt_dias_isolamento_w := trunc(dt_termino_w - dt_inicio_p);

end if;

return	qt_dias_isolamento_w;

end;
$body$
