SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dias_pos_operatorio_suep ( nr_cirurgia_p bigint, nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


dt_inicio_real_w	timestamp;
dt_alta_w		timestamp;
qt_dias_po_w		bigint;


BEGIN

begin
select	max(dt_inicio_real)
into STRICT	dt_inicio_real_w
from	cirurgia
where	nr_cirurgia		= nr_cirurgia_p
and	ie_status_cirurgia	= 2;
exception
	when others then
		dt_inicio_real_w	:= null;
end;

if (dt_inicio_real_w IS NOT NULL AND dt_inicio_real_w::text <> '') then
	begin
	
	
	begin
    select	dt_alta
	into STRICT	dt_alta_w	
	from	atendimento_paciente
	where	nr_atendimento		= nr_atendimento_p;
    exception
		when others then
			dt_alta_w	:= null;
	end;
		
	qt_dias_po_w	:= trunc(coalesce(trunc(dt_alta_w),trunc(clock_timestamp())) - trunc(dt_inicio_real_w));
	end;
end if;

RETURN qt_dias_po_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
