CREATE OR REPLACE FUNCTION obter_dias_prazo_autor (nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
qt_dias_prazo_w		bigint;


BEGIN

if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	select 	max(qt_dias_prazo)
	into STRICT 	qt_dias_prazo_w
	from 	autorizacao_convenio
	where 	nr_seq_agenda = nr_seq_agenda_p;

	if (qt_dias_prazo_w > 0) then
		ds_retorno_w		:= to_char(qt_dias_prazo_w)|| ' ' || Wheb_mensagem_pck.get_texto(309125); --' dias';
	else
		ds_retorno_w		:= Wheb_mensagem_pck.get_texto(307465); --'NÃ£o informado';
	end if;

end if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
