CREATE OR REPLACE FUNCTION obter_dias_previstos ( ie_objetivo_p text, cd_topografia_cih_p bigint, cd_material_p bigint, nr_atendimento_p bigint ) RETURNS bigint AS $body$
DECLARE


ie_controle_w	bigint;
ds_retorno_w	bigint;
cd_doenca_cid_w	varchar(20);


BEGIN


select	max(obter_controle_medic(cd_material,wheb_usuario_pck.get_cd_estabelecimento,ie_controle_medico,null,null,null))
into STRICT		ie_controle_w
from 	material
where 	cd_material = cd_material_p;

select 	obter_cod_diagnostico_atend(nr_atendimento_p)
into STRICT	cd_doenca_cid_w
;

select	obter_dias_previsto(ie_objetivo_p, cd_topografia_cih_p, cd_doenca_cid_w, cd_material_p, ie_controle_w)
into STRICT	ds_retorno_w
;

return	ds_retorno_w;

end;
