CREATE OR REPLACE FUNCTION obter_dias_previsto ( ie_objetivo_p text, cd_topografia_cih_p bigint, cd_doenca_cid_p text, cd_material_p bigint, cd_regra_p bigint) RETURNS bigint AS $body$
DECLARE


qt_dias_prev_w			smallint;
cd_estabelecimento_w	bigint;
ie_dia_semana_w			smallint;
ie_feriado_w 			bigint;
cd_perfil_w       		bigint;


BEGIN

cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;
cd_perfil_w := wheb_usuario_pck.get_cd_perfil;

ie_feriado_w := coalesce(obter_se_feriado(cd_estabelecimento_w, clock_timestamp()),0);

ie_dia_semana_w	:= pkg_date_utils.get_weekday(clock_timestamp());

select	max(qt_dias_prev)
into STRICT	qt_dias_prev_w
from	regra_dias_antimicrobiano
where	coalesce(ie_objetivo,coalesce(ie_objetivo_p,'XPTO')) = coalesce(ie_objetivo_p,'XPTO')
and	coalesce(cd_material, cd_material_p)		= cd_material_p
and	coalesce(cd_topografia_cih,coalesce(cd_topografia_cih_p,0)) = coalesce(cd_topografia_cih_p,0)
and	coalesce(cd_doenca_cid,coalesce(cd_doenca_cid_p,'XPTO')) = coalesce(cd_doenca_cid_p,'XPTO');

if (coalesce(qt_dias_prev_w::text, '') = '') then
	select 	max(qt_dias_prev)
	into STRICT	qt_dias_prev_w
	from (	SELECT	qt_dias_prev
			from	regra_antimicrobiano
			where	coalesce(cd_material, cd_material_p)		= cd_material_p
			and		cd_regra					= cd_regra_p
			and		coalesce(cd_estabelecimento, cd_estabelecimento_w) = cd_estabelecimento_w
			and   	((coalesce(cd_perfil::text, '') = '') or (cd_perfil = cd_perfil_w))
			and		(((ie_dia_semana = 9) or (coalesce(ie_dia_semana,ie_dia_semana_w) = ie_dia_semana_w))
					or (ie_feriado_w > 0 AND ie_dia_semana = 0))
			order by cd_material, cd_estabelecimento, cd_perfil) alias15 LIMIT 1;
end if;

return	qt_dias_prev_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
