CREATE OR REPLACE FUNCTION obter_dias_prev_proc_interno ( nr_seq_proc_iterno_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


qt_dias_w		bigint := 0;
qt_dias_prev_inter_w	smallint;
qt_dias_prev_uti_w	smallint;


BEGIN

if (coalesce(nr_seq_proc_iterno_p,0) > 0) then

	select	coalesce(max(qt_dias_prev_inter),0),
		coalesce(max(qt_dias_prev_uti),0)
	into STRICT	qt_dias_prev_inter_w,
		qt_dias_prev_uti_w
	from	proc_interno
	where	nr_sequencia = nr_seq_proc_iterno_p;

	if (ie_opcao_p = 'I') then
		qt_dias_w	:= qt_dias_prev_inter_w;
	elsif (ie_opcao_p = 'U') then
		qt_dias_w	:= qt_dias_prev_uti_w;
	end if;

end if;

return	qt_dias_w;

end;
$body$
LANGUAGE PLPGSQL
