CREATE OR REPLACE FUNCTION obter_dias_protocolo_quimio ( nr_seq_paciente_p bigint, cd_material_p bigint, cd_intervalo_p text) RETURNS varchar AS $body$
DECLARE


ds_dias_aplicacao_w	varchar(4000);


BEGIN

select	max(ds_dias_aplicacao)
into STRICT	ds_dias_aplicacao_w
from	paciente_protocolo_medic
where	nr_seq_paciente		= nr_seq_paciente_p
and	cd_material 		= cd_material_p
and	coalesce(cd_intervalo,'X')	= coalesce(cd_intervalo_p,'X');

return	ds_dias_aplicacao_w;

end;
$body$
