CREATE OR REPLACE FUNCTION obter_dias_seguranca_segmento ( nr_seq_planej_compra_p bigint, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w		double precision;
nr_seq_segmento_w	bigint;
cd_estabelecimento_w	smallint;
qt_dia_ressup_forn_w	smallint;
qt_dia_compra_w		double precision;
qt_dia_fequencia_w	double precision;
qt_dia_seguranca_w	double precision := 0;


BEGIN

select	coalesce(nr_seq_segmento,0),
	coalesce(qt_dia_compra,0),
	cd_estabelecimento
into STRICT	nr_seq_segmento_w,
	qt_dia_compra_w,
	cd_estabelecimento_w
from	sup_planejamento_compras
where	nr_sequencia = nr_seq_planej_compra_p;

if (nr_seq_segmento_w <> 0) then
	begin

	select	coalesce(qt_dia_frequencia,0)
	into STRICT	qt_dia_fequencia_w
	from	segmento_compras
	where	nr_sequencia = nr_seq_segmento_w;

	select	coalesce(qt_dia_ressup_forn,0)
	into STRICT	qt_dia_ressup_forn_w
	from	material_estab
	where	cd_material = cd_material_p
	and	cd_estabelecimento = cd_estabelecimento_w;

	qt_dia_seguranca_w := qt_dia_compra_w - (qt_dia_fequencia_w + qt_dia_ressup_forn_w);

	end;
end if;
ds_retorno_w := qt_dia_seguranca_w;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
