SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dias_ultima_etapa ( nr_interno_conta_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		bigint := 0;


BEGIN

select	max(trunc(clock_timestamp(),'dd') - trunc(a.dt_etapa,'dd'))
into STRICT	qt_retorno_w
from	fatur_etapa b,
	conta_paciente_etapa a
where	a.nr_seq_etapa		= b.nr_sequencia
and	a.nr_interno_conta		= nr_interno_conta_p
and	coalesce(b.ie_situacao,'A')	= 'A'
and	coalesce(a.dt_fim_etapa::text, '') = ''
and	a.dt_etapa		=	(SELECT	/*+ index(a conpaet_conpaci_fk_i) */						max(x.dt_etapa)
					 from	conta_paciente_etapa x
					 where   x.nr_interno_conta = nr_interno_conta_p)
order by a.dt_etapa;

return qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
