CREATE OR REPLACE FUNCTION obter_dias_uteis_periodo_rn259 ( dt_inicial_p timestamp, cd_especialidade_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


dt_aux_w			timestamp;
ie_dia_semana_w		smallint;
ie_feriado_w		smallint;
qt_dias_atend_w		smallint;
ie_dia_util			varchar(1);


BEGIN

if (cd_especialidade_p IS NOT NULL AND cd_especialidade_p::text <> '') and (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '')then

	dt_aux_w		:= trunc(dt_inicial_p, 'dd');
	ie_dia_util		:= obter_se_dia_util(dt_aux_w, cd_estabelecimento_p);
	qt_dias_atend_w := coalesce(obter_qt_dias_cons_ans(cd_especialidade_p),0);

	if qt_dias_atend_w > 0 then
		qt_dias_atend_w := qt_dias_atend_w - 1;
	end if;


	while	((qt_dias_atend_w > 0) or (ie_dia_util = 'N')) loop
		begin

		ie_dia_semana_w := obter_cod_dia_semana(dt_aux_w);
		ie_feriado_w := obter_se_feriado(cd_estabelecimento_p, dt_aux_w);

		If (ie_feriado_w = 0) and (ie_dia_semana_w in (2, 3, 4, 5,6)) then

			qt_dias_atend_w	:= qt_dias_atend_w - 1;
			dt_aux_w		:= dt_aux_w + 1;

		elsif	((ie_feriado_w > 0) or (ie_dia_semana_w in (1, 7))) then

			if (ie_dia_semana_w = 7) then
				dt_aux_w		:= dt_aux_w + 2;
			else
				dt_aux_w		:= dt_aux_w + 1;
			end if;

		end if;

		ie_dia_util := obter_se_dia_util(dt_aux_w, cd_estabelecimento_p);

		end;
	end loop;
else
	dt_aux_w	:= coalesce(dt_inicial_p,clock_timestamp());
end if;

RETURN to_char(dt_aux_w, 'dd/mm/yyyy');

END;
$body$
