CREATE OR REPLACE FUNCTION obter_dias_uteis_periodo (dt_inicial_p timestamp, dt_final_p timestamp, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


dt_aux_w		timestamp;
qt_diarias_w		bigint;
qt_feriado_w		bigint;


BEGIN

dt_aux_w	:= pkg_date_utils.start_of(dt_inicial_p,'DD',0);
qt_diarias_w	:= 0;

while(dt_aux_w <= dt_final_p) loop
	begin
	
	if (pkg_date_utils.is_business_day(dt_aux_w) = 1) then
		qt_diarias_w	:= qt_diarias_w + 1;
	end if;
		
	dt_aux_w	:= dt_aux_w + 1;
	end;
end loop;

select	count(1)
into STRICT	qt_feriado_w
from 	feriado
where 	dt_feriado between ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_inicial_p) and ESTABLISHMENT_TIMEZONE_UTILS.endOfDay(dt_final_p)
and	cd_estabelecimento = cd_estabelecimento_p
and	pkg_date_utils.is_business_day(dt_feriado) = 1;

if (qt_feriado_w > 0) and (qt_diarias_w > 0) then
	qt_diarias_w := qt_diarias_w - qt_feriado_w;
end if;

return qt_diarias_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
