CREATE OR REPLACE FUNCTION obter_dias_uteis_semana () RETURNS varchar AS $body$
DECLARE


ds_dias_semana_w	varchar(15);
nr_numero_w 		integer(7);

BEGIN

for nr_numero_w in 1..7
loop
  if (OBTER_SE_DIA_TRABALHO(nr_numero_w) = 'S') then
		ds_dias_semana_w := ds_dias_semana_w || nr_numero_w || ',';
  end if;
end loop;

return substr(ds_dias_semana_w,1, length(ds_dias_semana_w)-1);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
