CREATE OR REPLACE FUNCTION obter_dias_vencimento_atend (nr_atendimento_p bigint, cd_convenio_p bigint) RETURNS bigint AS $body$
DECLARE

 
dt_alta_w		timestamp;
qt_dias_venc_w		bigint := 0;
qt_dias_restante_w	bigint := 0;
			

BEGIN 
 
select	max(a.dt_alta), 
	max(b.nr_dias_venc_atend) 
into STRICT	dt_alta_w, 
	qt_dias_venc_w 
from	atendimento_paciente a, 
	convenio_estabelecimento b 
where	a.cd_estabelecimento = b.cd_estabelecimento 
and	a.nr_atendimento = nr_atendimento_p 
and	b.cd_convenio = cd_convenio_p;
 
if (dt_alta_w IS NOT NULL AND dt_alta_w::text <> '')and (coalesce(qt_dias_venc_w,0) <> 0)and 
	((dt_alta_w + qt_dias_venc_w) > clock_timestamp())then 
	begin 
	qt_dias_restante_w := ((dt_alta_w + qt_dias_venc_w) - clock_timestamp());
	end;
end if;
 
return	qt_dias_restante_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
