CREATE OR REPLACE FUNCTION obter_dia_anterior_util ( cd_estabelecimento_p bigint, dt_referencia_p timestamp) RETURNS timestamp AS $body$
DECLARE


dt_referencia_w		timestamp;
dt_retorno_w		timestamp;
ie_feriado_w		smallint;


BEGIN

dt_referencia_w		:= dt_referencia_p;
dt_retorno_w		:= null;

while(coalesce(dt_retorno_w::text, '') = '') loop
	select	obter_se_feriado(cd_estabelecimento_p, dt_referencia_w)
	into STRICT	ie_feriado_w
	;

	if (pkg_date_utils.is_business_day(dt_referencia_w) = 0) or (ie_feriado_w = 1) then
		dt_referencia_w	:= dt_referencia_w - 1;
	else
		dt_retorno_w	:= dt_referencia_w;
	end if;

end loop;

return dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
