CREATE OR REPLACE FUNCTION obter_dia_ciclo_multi_selec (ie_tipo_p text) RETURNS bigint AS $body$
DECLARE


nr_dia_ciclo_w	bigint;


BEGIN

IF (ie_tipo_p = 'D') THEN
	BEGIN
	SELECT	coalesce(MAX(nr_dias_multi_selecao),300)
	INTO STRICT	nr_dia_ciclo_w
	FROM	parametro_medico
	WHERE	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;
	END;
ELSIF (ie_tipo_p = 'C') THEN
	BEGIN
	SELECT	coalesce(MAX(nr_ciclos_multi_selecao),300)
	INTO STRICT	nr_dia_ciclo_w
	FROM	parametro_medico
	WHERE	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;
	END;
END IF;

IF (nr_dia_ciclo_w > 600) THEN
	nr_dia_ciclo_w := 600;
END IF;

RETURN	nr_dia_ciclo_w;

END;
$body$
LANGUAGE PLPGSQL
