CREATE OR REPLACE FUNCTION obter_dia_ciclo_onc ( nr_seq_atendimento_p bigint, ds_dia_ciclo_p text, dt_prevista_p timestamp) RETURNS bigint AS $body$
DECLARE


dt_prevista_w	timestamp;
nr_dia_ciclo_w	bigint;
ds_dia_ciclo_previsto_w	bigint;



BEGIN


select 	dt_real
into STRICT	dt_prevista_w
from	paciente_atendimento
where	nr_seq_atendimento = nr_seq_atendimento_p;


nr_dia_ciclo_w := somente_numero(ds_dia_ciclo_p);
ds_dia_ciclo_previsto_w :=(trunc(dt_prevista_p - dt_prevista_w) + nr_dia_ciclo_w);

return	ds_dia_ciclo_previsto_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
