CREATE OR REPLACE FUNCTION obter_dia_entrega_proc (dt_resultado_p timestamp, cd_procedimento_p bigint, ie_origem_proced_p text) RETURNS timestamp AS $body$
DECLARE


cd_tipo_proced_w  	bigint;
achou_w    		boolean;
dt_retorno_W   		timestamp;
qt_dia_w   		bigint;
qt_dia_ww		bigint;
dt_resultado_w			timestamp;


BEGIN



achou_w := false;
dt_resultado_w	:=	dt_resultado_p;


select obter_tipo_procedimento(cd_procedimento_p,ie_origem_proced_p,'C')
into STRICT cd_tipo_proced_w
;


if (cd_tipo_proced_w in (97,104,15,3,0,5,1)) then

 qt_dia_w 	:= 	0;

while( achou_w = false) loop

	dt_retorno_w := (dt_resultado_w+1);
	dt_resultado_w := (dt_resultado_w+1);

	if (obter_se_dia_util(dt_retorno_w,1) = 'S') then
		qt_dia_w	:= qt_dia_w+1;
	end if;

	if	((obter_se_dia_util(dt_retorno_w,1) = 'S') and (qt_dia_w >= 3)) then
		achou_w := true;
	end if;

 end loop;
end if;

if (cd_tipo_proced_w = 4) then

 qt_dia_w := 0;

 while( achou_w = false) loop

	dt_retorno_w := (dt_resultado_w+1);
	dt_resultado_w := (dt_resultado_w+1);


	if (obter_se_dia_util(dt_retorno_w,1) = 'S') then
		qt_dia_w	:= qt_dia_w+1;
	end if;

	if	((obter_se_dia_util(dt_retorno_w,1) = 'S') and (qt_dia_w >= 4)) then
		achou_w := true;
	end if;

 end loop;
end if;

if (achou_w = false) then
	dt_retorno_w := dt_resultado_p;
end if;


return dt_retorno_w;

end;
