CREATE TYPE qt_semana_w AS (dia_w timestamp);


CREATE OR REPLACE FUNCTION obter_dia_ini_fim_semana ( dt_mes_ref_p timestamp, vl_dominio_p bigint) RETURNS varchar AS $body$
DECLARE


--vetor
type vetor is table of qt_semana_w index by integer;

vetor_w			vetor;
ie_retorno_w		varchar(60);
i 			smallint   	:=0;
ind 			smallint   	:=1;
dt_dia_w 		timestamp 	:= trunc(dt_mes_ref_p,'month');
dt_inicio_semana_w 	timestamp;
dt_fim_semana_w 	timestamp;
ie_dia_sem_w	varchar(10);


BEGIN

while(dt_dia_w <= last_day(trunc(dt_mes_ref_p,'dd'))) loop
	ie_dia_sem_w	:= obter_cod_dia_semana(dt_dia_w);
/*	if	(philips_param_pck.get_nr_seq_idioma = 2) then -- Espanhol  - MÃ©xico(MX)
		if	(ie_dia_sem_w = 7) then
			ie_dia_sem_w := 1;
		else
			ie_dia_sem_w	:= ie_dia_sem_w + 1;
		end if;
	end if;
*/
	if (ie_dia_sem_w = 1) then
		begin
		i := i +1;
		vetor_w[i].dia_w := trunc(dt_dia_w);
		end;
	end if;
	dt_dia_w:= trunc(dt_dia_w) +1;
end loop;

ind := 1;
while(ind <= i) loop
	if (vl_dominio_p = ind) then
		dt_inicio_semana_w  :=  obter_inicio_fim_semana(trunc(vetor_w[ind].dia_w),'I');
		dt_fim_semana_w     :=  obter_inicio_fim_semana(trunc(vetor_w[ind].dia_w),'F');
		exit;
	end if;
	ind := 	ind + 1;
end loop;

ie_retorno_w	:= 	dt_inicio_semana_w || ' - ' ||
			dt_fim_semana_w;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
