CREATE TYPE qt_semana_w AS (dia_w timestamp);


CREATE OR REPLACE FUNCTION obter_dia_mes ( dt_mes_ref_p timestamp, vl_dominio_p bigint, vl_dominio_dia_p bigint) RETURNS timestamp AS $body$
DECLARE


--vetor
type vetor is table of qt_semana_w index by integer;

vetor_w			vetor;
ie_retorno_w		timestamp;
i 			smallint   	:=0;
ind 			smallint   	:=1;
dt_dia_w 		timestamp 	:= pkg_date_utils.start_of(dt_mes_ref_p, 'MONTH', 0);
dt_inicio_semana_w 	timestamp;
dt_fim_semana_w 	timestamp;
ie_dia_semana_w		varchar(10);


BEGIN

while(dt_dia_w <= trunc(pkg_date_utils.end_of(dt_mes_ref_p, 'MONTH', 0))) loop
	ie_dia_semana_w := pkg_date_utils.get_weekday(dt_dia_w);

/*	if	(philips_param_pck.get_nr_seq_idioma = 2) then -- Espanhol  - México(MX)
		if	(ie_dia_semana_w = 7) then
			ie_dia_semana_w := 1;
		else
			ie_dia_semana_w	:= ie_dia_semana_w + 1;
		end if;
	end if;*/
	if (ie_dia_semana_w = 1) then
		begin
		i := i +1;
		vetor_w[i].dia_w := trunc(dt_dia_w);
		end;
	end if;
	dt_dia_w:= trunc(dt_dia_w) +1;
end loop;

dt_dia_w := vetor_w[vl_dominio_p].dia_w;

while(dt_dia_w <= obter_inicio_fim_semana(trunc(vetor_w[vl_dominio_p].dia_w),'F')) loop
	begin
	ie_dia_semana_w := pkg_date_utils.get_weekday(dt_dia_w);

	/*if	(philips_param_pck.get_nr_seq_idioma = 2) then -- Espanhol  - México(MX)
		if	(ie_dia_semana_w = 7) then
			ie_dia_semana_w := 1;
		else
			ie_dia_semana_w	:= ie_dia_semana_w + 1;
		end if;
	end if;
*/
	if (ie_dia_semana_w = vl_dominio_dia_p) then
		begin
		ie_retorno_w := trunc(dt_dia_w);
		exit;
		end;
	end if;
	end;
	dt_dia_w:= trunc(dt_dia_w) +1;
end loop;

return	ie_retorno_w;

end;
$body$
