SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dia_semana_agenda_quimio (dt_agenda_p timestamp, cd_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ds_dia_semana_w	varchar(15);
qt_hor_agenda_w	bigint;
ds_dia_agenda_w	varchar(240);


BEGIN
if (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') then

	select	obter_dia_semana(dt_agenda_p)
	into STRICT	ds_dia_semana_w
	;

	select	count(*)
	into STRICT 	qt_hor_agenda_w
	from 	agenda_quimio
	where 	trunc(dt_agenda) = trunc(dt_agenda_p)
	and 	nr_seq_local = cd_agenda_p;

	ds_dia_agenda_w	:= ds_dia_semana_w || ' (' || to_char(qt_hor_agenda_w) || ')';

end if;

return ds_dia_agenda_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
