CREATE OR REPLACE FUNCTION obter_dia_util_ant_periodo ( dt_inicial_p timestamp, qt_dias_anterior_p bigint, cd_estabelecimento_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w		timestamp;
dt_referencia_w		timestamp;
qt_dias_util_w		integer;
ie_dia_semana_w		smallint;
qt_feriado_w		integer;


BEGIN

dt_referencia_w		:= trunc(coalesce(dt_inicial_p,clock_timestamp()));
dt_retorno_w		:= null;
qt_dias_util_w		:= 0;

if (coalesce(qt_dias_anterior_p,0) > 0) then
	begin

	while(coalesce(dt_retorno_w::text, '') = '') loop

		dt_referencia_w	:= (dt_referencia_w - 1);

		ie_dia_semana_w := pkg_date_utils.is_business_day(dt_referencia_w,0);

		select	obter_se_feriado(cd_estabelecimento_p, dt_referencia_w)
		into STRICT	qt_feriado_w
		;

		if (ie_dia_semana_w = 1) and (qt_feriado_w = 0) then
			qt_dias_util_w	:= qt_dias_util_w + 1;
		end if;

		if (qt_dias_util_w	= qt_dias_anterior_p) then
			dt_retorno_w	:= dt_referencia_w;
		end if;

	end loop;

	end;
else
	dt_retorno_w	:= clock_timestamp();
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
