CREATE OR REPLACE FUNCTION obter_dia_util_mes (cd_estabelecimento_p bigint, dt_mes_ref_p timestamp, qt_dia_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_mes_w	timestamp;
dt_retorno_w	timestamp	:= null;
qt_dia_w	smallint;
ult_dia_util_valido_w	timestamp;


BEGIN

dt_mes_w		:= trunc(dt_mes_ref_p, 'month');
qt_dia_w		:= 0;

while(dt_mes_w <= fim_mes(dt_mes_ref_p)) loop

	-- verificar se e dia util
	if (dt_mes_w = obter_proximo_dia_util(cd_estabelecimento_p, dt_mes_w)) then
		qt_dia_w		:= qt_dia_w + 1;
		ult_dia_util_valido_w := obter_proximo_dia_util(cd_estabelecimento_p, dt_mes_w);
	end if;

	if (qt_dia_w = qt_dia_p) then
		dt_retorno_w		:= dt_mes_w;
		exit; --NOSONAR
	else
		dt_retorno_w		:= ult_dia_util_valido_w;
	end if;

	dt_mes_w			:= dt_mes_w + 1;

end loop;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
