CREATE OR REPLACE FUNCTION obter_dic_obj_filtro_idioma ( nr_seq_filtro_p bigint, nr_seq_atrib_filtro_p bigint, nr_seq_idioma_p bigint, ie_campo_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_campo_p
DS_LABEL
DS_OPCOES
*/
ds_retorno_w	varchar(4000);
ie_componente_w	varchar(15);
ds_opcoes_w	varchar(2000);
ds_label_w	varchar(255);


BEGIN

if (nr_seq_idioma_p IS NOT NULL AND nr_seq_idioma_p::text <> '') and (nr_seq_idioma_p > 0) then
	select	max(ds_traducao)
	into STRICT	ds_retorno_w
	from	dic_objeto_filtro_idioma
	where	nr_seq_objeto_filtro = nr_seq_filtro_p
	and	nr_seq_objeto = nr_seq_atrib_filtro_p
	and	nr_seq_idioma = nr_seq_idioma_p
	and	nm_atributo = ie_campo_p;

	if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') and (ds_retorno_w <> ' ') then
		return ds_retorno_w;
	end if;
end if;

select	ds_opcoes,
	ds_label
into STRICT	ds_opcoes_w,
	ds_label_w
from	dic_objeto_filtro
where	nr_sequencia = nr_seq_atrib_filtro_p;

if (ie_campo_p = 'DS_OPCOES') then
	ds_retorno_w	:= ds_opcoes_w;
else
	ds_retorno_w	:= ds_label_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
