SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dietas_complementares ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


nm_dieta_w	varchar(100);
ds_retorno_w	varchar(2000):= '';

c01 CURSOR FOR
	SELECT	substr(obter_nome_dieta(cd_dieta),1,100)
	from	mapa_dieta
	where	((nr_sequencia = nr_sequencia_p) or (nr_seq_superior = nr_sequencia_p))
	order by substr(obter_nome_dieta(cd_dieta),1,100);

BEGIN

OPEN C01;
LOOP
FETCH C01 into	nm_dieta_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	begin
	if (nm_dieta_w IS NOT NULL AND nm_dieta_w::text <> '') then
		ds_retorno_w:= nm_dieta_w || ', ' || substr(ds_retorno_w,1,length(ds_retorno_w)-1);
	end if;
	end;
END LOOP;
CLOSE C01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
