CREATE OR REPLACE FUNCTION obter_dietas_evolucao ( nr_atendimento_p bigint, dt_servico_p timestamp, ie_tipo_evolucao_p bigint) RETURNS varchar AS $body$
DECLARE

ds_dieta_w	varchar(255);
nr_prescricao_w	bigint;
ds_retorno_w	varchar(2000);

c01 CURSOR FOR
	SELECT	distinct c.nm_curto
	from	prescr_medica a,
		prescr_dieta b,
		dieta c
	where	a.nr_prescricao = b.nr_prescricao
	and	a.nr_prescricao	= nr_prescricao_w
	and	c.cd_dieta	= b.cd_dieta
	and	a.nr_atendimento = nr_atendimento_p
	and	dt_servico_p between a.dt_inicio_prescr and a.dt_validade_prescr
	and	coalesce(a.dt_suspensao::text, '') = ''
	and	(obter_cod_funcao_usuario_orig(a.nm_usuario_original))::numeric  = ie_tipo_evolucao_p
	order by 1;



BEGIN
	select	max(a.nr_prescricao)
	into STRICT	nr_prescricao_w
	from	prescr_medica a,
		prescr_dieta b,
		dieta c
	where	a.nr_prescricao = b.nr_prescricao
	and	c.cd_dieta	= b.cd_dieta
	and	a.nr_atendimento = nr_atendimento_p
	and	dt_servico_p between a.dt_inicio_prescr and a.dt_validade_prescr
	and	coalesce(a.dt_suspensao::text, '') = ''
	and	(obter_cod_funcao_usuario_orig(a.nm_usuario_original))::numeric  = ie_tipo_evolucao_p
	order by 1;

	open c01;
	loop
	fetch c01 into
		ds_dieta_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
			ds_retorno_w := ds_retorno_w||ds_dieta_w||',';
		end;
	end loop;
	close c01;

return substr(ds_retorno_w,1,length(ds_retorno_w)-1);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
