CREATE OR REPLACE FUNCTION obter_dietas_pac_mapa (nr_seq_mapa_p bigint) RETURNS varchar AS $body$
DECLARE


ds_dieta_w	varchar(255);
ds_retorno_w	varchar(2000);

C01 CURSOR FOR
	SELECT substr(obter_nome_dieta(a.cd_dieta),1,100) ds_dieta
	from mapa_dieta a
	where nr_sequencia = nr_seq_mapa_p
	
union

	SELECT substr(obter_nome_dieta(a.cd_dieta),1,100) ds_dieta
	from mapa_dieta a
	where nr_seq_superior = nr_seq_mapa_p;


BEGIN
open C01;
loop
fetch C01 into
	ds_dieta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w := ds_retorno_w||ds_dieta_w||', ';
	end;
end loop;
close C01;


ds_retorno_w := substr(ds_retorno_w,1,length(ds_retorno_w)-2);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
