CREATE OR REPLACE FUNCTION obter_dietas_ultima_prescr ( nr_atendimento_p bigint, ie_tipo_dieta_p text default null) RETURNS varchar AS $body$
DECLARE


nm_dieta_w	varchar(100);
ds_retorno_w	varchar(2000):= '';

c01 CURSOR FOR
	SELECT	substr(obter_nome_dieta(a.cd_dieta),1,100)
	from	prescr_dieta a
	where	coalesce(a.ie_suspenso,'N') = 'N'
	and	(((SELECT d.ie_tipo_dieta from dieta d where d.cd_dieta = a.cd_dieta) = ie_tipo_dieta_p) or (coalesce(ie_tipo_dieta_p::text, '') = ''))
	and	a.nr_prescricao = (	select	coalesce(max(c.nr_prescricao),0)
					from 	prescr_dieta b,
						prescr_medica c
					where 	c.nr_prescricao 	= b.nr_prescricao
					and 	c.nr_atendimento	= nr_atendimento_p
					and	(coalesce(c.dt_liberacao,c.dt_liberacao_medico) IS NOT NULL AND (coalesce(c.dt_liberacao,c.dt_liberacao_medico))::text <> '')
					and	substr(elimina_acentos(upper(obter_funcao_usuario_orig(c.nm_usuario_original))),1,3) = 'MED')
	
union

	select	substr(obter_nome_dieta(a.cd_dieta),1,100)
	from	prescr_dieta a
	where	coalesce(a.ie_suspenso,'N') = 'N'
	and	a.nr_prescricao = (	select	coalesce(max(c.nr_prescricao),0)
					from 	prescr_dieta b,
						prescr_medica c
					where 	c.nr_prescricao 	= b.nr_prescricao
					and 	c.nr_atendimento	= nr_atendimento_p
					and	(coalesce(c.dt_liberacao,c.dt_liberacao_medico) IS NOT NULL AND (coalesce(c.dt_liberacao,c.dt_liberacao_medico))::text <> '')
					and	substr(elimina_acentos(upper(obter_funcao_usuario_orig(c.nm_usuario_original))),1,3) = 'NUT')
	
union

	select	substr(obter_nome_dieta(a.cd_dieta),1,100)
	from	prescr_dieta a
	where	coalesce(a.ie_suspenso,'N') = 'N'
	and	a.nr_prescricao = (	select	coalesce(max(c.nr_prescricao),0)
					from 	prescr_dieta b,
						prescr_medica c
					where 	c.nr_prescricao 	= b.nr_prescricao
					and 	c.nr_atendimento	= nr_atendimento_p
					and	(coalesce(c.dt_liberacao,c.dt_liberacao_medico) IS NOT NULL AND (coalesce(c.dt_liberacao,c.dt_liberacao_medico))::text <> '')
					and	substr(elimina_acentos(upper(obter_funcao_usuario_orig(c.nm_usuario_original))),1,3) = 'ENF')
	order by 1;


BEGIN

open c01;
loop
fetch c01 into	nm_dieta_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	begin
	if (nm_dieta_w IS NOT NULL AND nm_dieta_w::text <> '') then
		ds_retorno_w:= nm_dieta_w || ', ' || substr(ds_retorno_w,1,length(ds_retorno_w)-1);
	end if;
	end;
end loop;
close c01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
