CREATE OR REPLACE FUNCTION obter_dieta_lib_atend (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := '';
cd_convenio_w		integer;
ie_dieta_w		varchar(15);


BEGIN

select	max(ie_lib_dieta)
into STRICT	ie_dieta_w
from	atend_categoria_convenio a
where	a.nr_atendimento = nr_atendimento_p
and 	a.dt_inicio_vigencia	= (SELECT max(dt_inicio_vigencia)
				from Atend_categoria_convenio b
				where nr_atendimento	= nr_atendimento_p);

if (ie_dieta_w IS NOT NULL AND ie_dieta_w::text <> '') then
	select 	substr(obter_desc_expressao(cd_exp_valor_dominio, ds_valor_dominio),1,254) ds
	into STRICT	ds_retorno_w
	from   	valor_dominio
	where  	IE_SITUACAO = 'A'
	and    	CD_DOMINIO = 1396
	and	vl_dominio = ie_dieta_w;
end if;


return	ds_retorno_w;

end;
$body$
