CREATE OR REPLACE FUNCTION obter_dieta_pep ( cd_dieta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
cd_estabelecimento_w	bigint;



BEGIN
cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

select	nm_dieta ds
into STRICT	ds_retorno_w
from	dieta
where	ie_situacao = 'A'
and	((coalesce(cd_estabelecimento::text, '') = '') or (cd_estabelecimento = cd_estabelecimento_w))
and	cd_dieta = cd_dieta_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
