CREATE OR REPLACE FUNCTION obter_diferenca_bloqueto ( nr_titulo_p bigint, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


/*	Calcula a diferenca entre o valor original do bloqueto e o valro do titulo,
	sem considerar os impostos.
	Aplica-se para os casos em que ha ajuste de valor (Parametro [133] da funcao Titulos a Pagar) */
vl_diferenca_w		double precision;
vl_titulo_w		double precision;
vl_bloqueto_w		double precision;
vl_imposto_w		double precision;
cd_estabelecimento_w	smallint;
nm_usuario_w		varchar(15);
ie_gerar_trib_bloq_w	varchar(1);
nr_seq_nota_fiscal_w	bigint;
nr_bloqueto_w		titulo_pagar.nr_bloqueto%type;


BEGIN

vl_diferenca_w	:= 0;

select	max(a.vl_titulo),
	CASE WHEN substr(max(trim(both a.nr_bloqueto)), 1, 1)=8 THEN  coalesce((substr(rpad(substr(converte_codigo_bloqueto('Barra_Lido',max(a.nr_bloqueto)),1,255),43,'0'),34,10))::numeric ,0) / 100  ELSE coalesce((substr(rpad(substr(converte_codigo_bloqueto('Barra_Lido',max(a.nr_bloqueto)),1,255),47,'0'),38,10))::numeric ,0) / 100 END ,
	max(a.cd_estabelecimento),
	coalesce(nm_usuario_p,max(a.nm_usuario)),
	max(a.nr_seq_nota_fiscal),
	max(trim(both a.nr_bloqueto))
into STRICT	vl_titulo_w,
	vl_bloqueto_w,
	cd_estabelecimento_w,
	nm_usuario_w,
	nr_seq_nota_fiscal_w,
	nr_bloqueto_w
from	titulo_pagar a
where	a.nr_titulo	= nr_titulo_p;

if ( length(nr_bloqueto_w) = 48) then
	vl_bloqueto_w := coalesce((Substr(
						Substr(Substr(nr_bloqueto_w,0,12),0,11) ||
						Substr(Substr(nr_bloqueto_w,13,12),0,11) ||
						Substr(Substr(nr_bloqueto_w,25,12),0,11) ||
						Substr(Substr(nr_bloqueto_w,37,12),0,11)
						,5,11)
					)::numeric 
			,0) / 100;	
end if;

ie_gerar_trib_bloq_w := obter_param_usuario(851, 123, obter_perfil_ativo, nm_usuario_w, cd_estabelecimento_w, ie_gerar_trib_bloq_w);

if (vl_titulo_w	< vl_bloqueto_w) then

	if (ie_gerar_trib_bloq_w = 'S') then

		select	coalesce(sum(a.vl_tributo),0)
		into STRICT	vl_imposto_w
		from	nota_fiscal_trib a
		where	a.nr_sequencia	= nr_seq_nota_fiscal_w;

	else

		vl_imposto_w	:= 0;

	end if;

	vl_diferenca_w	:= obter_valor_limite(vl_bloqueto_w - vl_titulo_w - vl_imposto_w,0,null);

end if;

RETURN vl_diferenca_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
