CREATE OR REPLACE FUNCTION obter_diferenca_ordem_solic ( nr_ordem_compra_p bigint, nr_item_oci_p bigint) RETURNS varchar AS $body$
DECLARE


nr_solic_compra_w		bigint;
nr_item_solic_compra_w		integer;
qt_material_w			double precision;
vl_unitario_material_w		double precision;
vl_unit_previsto_ww		double precision;
qt_material_ww			double precision;
ds_divergencia_w		varchar(255) := '';


BEGIN
select	coalesce(max(nr_solic_compra),0),
	coalesce(max(nr_item_solic_compra),0),
	coalesce(max(qt_material),0),
	coalesce(max(vl_unitario_material),0)
into STRICT	nr_solic_compra_w,
	nr_item_solic_compra_w,
	qt_material_w,
	vl_unitario_material_w
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_p
and	nr_item_oci = nr_item_oci_p;

if (nr_solic_compra_w > 0) then
	begin
	select	coalesce(max(vl_unit_previsto),0),
		coalesce(max(qt_material),0)
	into STRICT	vl_unit_previsto_ww,
		qt_material_ww
	from	solic_compra_item
	where	nr_solic_compra		= nr_solic_compra_w
	and	nr_item_solic_compra	= nr_item_solic_compra_w;
	if (vl_unit_previsto_ww <> 0) and (vl_unit_previsto_ww <> vl_unitario_material_w) then
		ds_divergencia_w	:= substr(ds_divergencia_w || WHEB_MENSAGEM_PCK.get_texto(304158,'VL_UNIT_PREVISTO_WW='||vl_unit_previsto_ww||
											';VL_UNITARIO_MATERIAL_W='||vl_unitario_material_w)|| ' ',1,254);
	end if;
	if (qt_material_ww <> 0) and (qt_material_ww <> qt_material_w) then
		ds_divergencia_w	:= substr(ds_divergencia_w || WHEB_MENSAGEM_PCK.get_texto(304162,'QT_MATERIAL_WW='||qt_material_ww||
											';QT_MATERIAL_W='||qt_material_w),1,254);
	end if;
	end;
end if;

return ds_divergencia_w;

end;
$body$
