SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_diferenca_vencimento (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE



ie_retorno_w		varchar(1) := 'N';
dt_vencimento_nf_w		timestamp;
dt_vencimento_oc_w		timestamp;


c01 CURSOR FOR
SELECT	max(dt_vencimento)
into STRICT	dt_vencimento_nf_w
from	nota_fiscal_venc
where	nr_sequencia = nr_sequencia_p
order by 1;


c02 CURSOR FOR
SELECT	max(o.dt_vencimento)
into STRICT	dt_vencimento_oc_w
from	nota_fiscal n,
		ORDEM_COMPRA_VENC o
where	n.nr_ordem_compra = o.nr_ordem_compra
and  	n.nr_sequencia = nr_sequencia_p
order by 1;



BEGIN
	open c01;
	loop
	fetch c01 into
	dt_vencimento_nf_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		open c02;
		loop
		fetch c02 into
		dt_vencimento_oc_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
		if (dt_vencimento_nf_w <> dt_vencimento_oc_w) then
			ie_retorno_w := 'S';
		end if;
		end loop;
		close c02;
		end;
	end loop;
	close c01;

return ie_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_diferenca_vencimento (nr_sequencia_p bigint) FROM PUBLIC;

