SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dif_atend_gv (nr_seq_gv_p bigint) RETURNS varchar AS $body$
DECLARE

			 
dt_entrada_w		timestamp;
dt_solicitacao_w	timestamp;
qt_minutos_w	bigint;
ds_retorno_w	varchar(15);


BEGIN 
if (coalesce(nr_seq_gv_p,0) > 0) then 
	select max(dt_entrada), 
		max(dt_solicitacao) 
	into STRICT	dt_entrada_w, 
		dt_solicitacao_w 
	from	gestao_vaga a, 
		atendimento_paciente b 
	where	b.nr_atendimento = a.nr_atendimento 
	and	a.nr_sequencia = nr_seq_gv_p;
	 
	if (dt_solicitacao_w IS NOT NULL AND dt_solicitacao_w::text <> '') and (dt_entrada_w IS NOT NULL AND dt_entrada_w::text <> '') then 
		qt_minutos_w	:= round((dt_entrada_w - dt_solicitacao_w) * 1440);
		 
		if (qt_minutos_w > 0 ) then 
			ds_retorno_w := trim(both replace(to_char(floor(dividir(qt_minutos_w,60)),'999,999,900'),',','.')) || ':' || substr(to_char(mod(qt_minutos_w,60),'00'),2,2);
		end if;
	end if;	
end if;
 
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dif_atend_gv (nr_seq_gv_p bigint) FROM PUBLIC;

