CREATE OR REPLACE FUNCTION obter_dif_datas_neg (dt_inicial_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(30);
qt_min_dif_w		varchar(30);


BEGIN

if (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') then
	if (dt_inicial_p < dt_final_p) then
		select 	obter_dif_data(dt_inicial_p, dt_final_p,'')
		into STRICT	qt_min_dif_w
		;

		ds_retorno_w := qt_min_dif_w;

	else
		select 	obter_dif_data(dt_final_p, dt_inicial_p,'')
		into STRICT	qt_min_dif_w
		;

		if (qt_min_dif_w IS NOT NULL AND qt_min_dif_w::text <> '') then
			ds_retorno_w := '-'||qt_min_dif_w;
		end if;

	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
