SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dif_desc (nr_interno_conta_p bigint) RETURNS bigint AS $body$
DECLARE


vl_diferenca_w		double precision	:= 0;
vl_desconto_w		double precision	:= 0;
vl_desc_resumo_w	double precision	:= 0;


BEGIN

select	coalesce(sum(vl_desconto), 0)
into STRICT	vl_desconto_w
from	conta_paciente_desconto
where 	nr_interno_conta	= nr_interno_conta_p
and	(dt_calculo IS NOT NULL AND dt_calculo::text <> '');

if (vl_desconto_w	> 0) then
	begin

	select	coalesce(sum(vl_desconto), 0)
	into STRICT	vl_desc_resumo_w
	from	conta_paciente_resumo
	where	nr_interno_conta	= nr_interno_conta_p;

	vl_diferenca_w	:= vl_desconto_w - vl_desc_resumo_w;

	end;
end if;


return	coalesce(vl_diferenca_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dif_desc (nr_interno_conta_p bigint) FROM PUBLIC;

