SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dif_dt_hist_status (nr_seq_consulta_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


dt_status_atual_w	timestamp;
dt_status_anterior_w	timestamp;
qt_retorno_w		varchar(30);


BEGIN

IF (nr_seq_consulta_p IS NOT NULL AND nr_seq_consulta_p::text <> '') AND (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '')	THEN
/*
 Obtendo a data atual do histórico
*/
	SELECT	MAX(a.dt_status)
	INTO STRICT	dt_status_atual_w
	FROM	oft_status_consulta_hist a
	WHERE	a.nr_seq_consulta = nr_seq_consulta_p
	AND	a.nr_sequencia = nr_sequencia_p;
/*
 Obtendo a data anterior
*/
	SELECT	MAX(a.dt_status)
	INTO STRICT	dt_status_anterior_w
	FROM	oft_status_consulta_hist a
	WHERE	a.nr_seq_consulta = nr_seq_consulta_p
	AND	a.dt_status < dt_status_atual_w;
END IF;
/*
 Obter diferença entre a atual pela anterior
*/
IF (dt_status_anterior_w IS NOT NULL AND dt_status_anterior_w::text <> '')  THEN
	qt_retorno_w := obter_dif_data(dt_status_anterior_w, dt_status_atual_w, 'A');
END IF;

RETURN	qt_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
