CREATE OR REPLACE FUNCTION obter_dif_ent_saida_lavanderia (nr_seq_saida_corresp_p bigint, ie_tipo_p text) RETURNS bigint AS $body$
DECLARE


qt_peso_saida_w		double precision;
qt_peso_entrada_w	double precision;
vl_retorno_w		double precision;

BEGIN

select	max(qt_peso)
into STRICT	qt_peso_saida_w
from	rop_lavanderia
where	nr_sequencia = nr_seq_saida_corresp_p;


/*Quantidade do peso da saida correspondente*/

if (ie_tipo_p = 'QT') then
	vl_retorno_w := qt_peso_saida_w;
/*Porcentagem de diferença do peso da entrada com relação a saida correspondente*/

elsif (ie_tipo_p = 'PR') then
	begin

	select	max(qt_peso)
	into STRICT	qt_peso_entrada_w
	from	rop_lavanderia
	where	nr_seq_saida_corresp = nr_seq_saida_corresp_p;

	vl_retorno_w := (100 - dividir((qt_peso_entrada_w * 100),qt_peso_saida_w));
	end;
/*Diferença do peso da entrada com relação a saida correspondente*/

elsif (ie_tipo_p = 'QD') then
	begin

	select	max(qt_peso)
	into STRICT	qt_peso_entrada_w
	from	rop_lavanderia
	where	nr_seq_saida_corresp = nr_seq_saida_corresp_p;

	vl_retorno_w := qt_peso_saida_w - qt_peso_entrada_w;
	end;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
