CREATE OR REPLACE FUNCTION obter_dif_espera_senha (nr_sequencia_p bigint, ie_tipo_tempo_p text, dt_referencia_p timestamp default clock_timestamp()) RETURNS varchar AS $body$
DECLARE


ds_tempo_espera_w	varchar(20);
ie_forma_tempo_espera_w	varchar(1);
dt_inicio_w		timestamp;
dt_inutilizacao_w	timestamp;
ds_tempo_w		varchar(20);
dt_referencia_w		timestamp;
intervalo bigint;

/* ie_tipo_tempo_p
 H - Tempo do paciente no hospital (desde a geracao da senha)
 A - Tempo de espera atual (depende do param 77)
*/
BEGIN

ie_forma_tempo_espera_w := Obter_param_Usuario(10021, 77, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_forma_tempo_espera_w);

If (ie_tipo_tempo_p = 'A') and (ie_forma_tempo_espera_w = 'F') then
	
		select	max(coalesce(dt_entrada_fila, dt_geracao_senha)),
			max(coalesce(dt_inutilizacao, dt_referencia_p))
		into STRICT	dt_inicio_w,
			dt_inutilizacao_w
		from	paciente_senha_fila a
		where	a.nr_sequencia = nr_sequencia_p;	
Else
	select	max(dt_geracao_senha),
		max(coalesce(dt_inutilizacao, dt_referencia_p))
	into STRICT	dt_inicio_w,
		dt_inutilizacao_w
	from	paciente_senha_fila a
	where	a.nr_sequencia = nr_sequencia_p;
end if;


dt_referencia_w	:= least(dt_inutilizacao_w, dt_inutilizacao_w);
intervalo := dt_referencia_w - dt_inicio_w;

begin
	SELECT
			CASE
		WHEN intervalo  < 0 THEN
			'-'
			 ELSE ''
				 END ||
				  CASE  WHEN(EXTRACT(DAY FROM elapsed_interval) * 24)  + EXTRACT(HOUR   FROM elapsed_interval) < 10 THEN '0' ELSE '' END ||
		TO_char(  (EXTRACT(DAY FROM elapsed_interval) * 24)  + EXTRACT(HOUR   FROM elapsed_interval) ||':'||   
		   LPAD(EXTRACT(MINUTE FROM elapsed_interval),2,'0')||':'||
		   LPAD(EXTRACT(SECOND FROM elapsed_interval),2,'0') )
		  formatted_seconds
		 into STRICT ds_tempo_espera_w
	 FROM (
	   SELECT numtodsinterval( abs(intervalo) ,'DAY') elapsed_interval
	   
	 ) alias13;
exception
when others then
	ds_tempo_espera_w := '00:00:00';
end;	


If (coalesce(ds_tempo_espera_w::text, '') = '' or (ds_tempo_espera_w = '0:00:00') or (ds_tempo_espera_w = '::')) then
	ds_tempo_espera_w := '00:00:00';
end if;

return	ds_tempo_espera_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
