CREATE OR REPLACE FUNCTION obter_digitos_nota_convertido ( cd_serie_nf_p text, nr_nota_fiscal_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
qt_digitos_w	bigint;
nr_nota_W	varchar(255);


BEGIN 
 
select  max(nr_min_caracteres) 
into STRICT	 qt_digitos_w 
from	 serie_nota_fiscal 
where	 cd_serie_nf = cd_serie_nf_p 
and	 cd_estabelecimento = cd_estabelecimento_p;
 
nr_nota_w := lpad(nr_nota_fiscal_p, qt_digitos_w, '0');
 
RETURN nr_nota_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
