SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_diluicao_agentes ( nr_seq_agente_cir_p bigint ) RETURNS varchar AS $body$
DECLARE



ds_retorno_w		varchar(4000);
ds_material_w		varchar(255);
qt_dose_w		double precision;
qt_dose_w2		double precision;
cd_unidade_medida_w	varchar(30);
cd_unidade_medida_w2	varchar(30);
qt_rediluir_w		double precision;


BEGIN

/* Reconstituição */

begin
select	obter_desc_material(cd_material),
	qt_dose,
	cd_unidade_medida
into STRICT	ds_material_w,
	qt_dose_w,
	cd_unidade_medida_w
from	agente_anest_diluicao
where	nr_seq_agente_cir	=	nr_seq_agente_cir_p
and	ie_origem_inf	=	1;
exception
	when others then
		ds_material_w	:= null;
end;


if (ds_material_w IS NOT NULL AND ds_material_w::text <> '') then
	ds_retorno_w	:=	/*'Reconstituir em '*/
 obter_desc_expressao(781863) || to_char(qt_dose_w) || ' ' || cd_unidade_medida_w ||
				' de ' || ds_material_w || chr(13) || chr(10);
end if;

/* Diluição */

begin
select	b.qt_dose,
	b.cd_unidade_medida,
	a.qt_dose,
	a.cd_unidade_medida,
	obter_desc_material(a.cd_material)
into STRICT	qt_dose_w,
	cd_unidade_medida_w,
	qt_dose_w2,
	cd_unidade_medida_w2,
	ds_material_w
from	agente_anest_diluicao a,
	cirurgia_agente_anestesico b
where	a.nr_seq_agente_cir	=	nr_seq_agente_cir_p
and	a.nr_seq_agente_cir	=	b.nr_sequencia
and	ie_origem_inf	=	2
and	coalesce(b.ie_situacao,'A') = 'A';
exception
	when others then
		ds_material_w	:= null;
end;

if	((ds_material_w IS NOT NULL AND ds_material_w::text <> '') and (qt_dose_w2 IS NOT NULL AND qt_dose_w2::text <> '') and (qt_dose_w IS NOT NULL AND qt_dose_w::text <> '')) then
	ds_retorno_w	:=	ds_retorno_w ||
				obter_desc_expressao(346577) || to_char(qt_dose_w) || ' ' || cd_unidade_medida_w ||
				obter_desc_expressao(341615) || to_char(qt_dose_w2) || ' ' ||  cd_unidade_medida_w2 || obter_desc_expressao(746911) || ds_material_w || chr(13) || chr(10);
end if;

/* Rediluição */

begin
select	a.qt_rediluir,
	a.qt_dose,
	a.cd_unidade_medida,
	obter_desc_material(a.cd_material)
into STRICT	qt_rediluir_w,
	qt_dose_w,
	cd_unidade_medida_w,
	ds_material_w
from	agente_anest_diluicao a,
	cirurgia_agente_anestesico b
where	a.nr_seq_agente_cir	=	nr_seq_agente_cir_p
and	a.nr_seq_agente_cir	=	b.nr_sequencia
and	ie_origem_inf	=	3
and	coalesce(b.ie_situacao,'A') = 'A';
exception
	when others then
		ds_material_w	:= null;
end;

if (ds_material_w IS NOT NULL AND ds_material_w::text <> '') then
	ds_retorno_w	:=	ds_retorno_w ||
				obter_desc_expressao(346570) || to_char(qt_rediluir_w) || ' ml' || obter_desc_expressao(347717) || to_char(qt_dose_w) || ' ' || cd_unidade_medida_w ||
				obter_desc_expressao(746911) || ds_material_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
