CREATE OR REPLACE FUNCTION obter_diluicao_material ( cd_material_p bigint, nr_seq_tipo_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_diluicao_w		varchar(100);


BEGIN
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	select	substr(coalesce(obter_desc_material(cd_diluente),''),1,100)
	into STRICT	ds_diluicao_w
	from	material_diluicao
	where	ie_reconstituicao in ('S','N')
	and	cd_material = cd_material_p
	and	nr_seq_interno = nr_seq_tipo_p;

end if;

return ds_diluicao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
